interface WorldMapProps {
  usersByCountry: CountryUserData[];
}

const Tooltip: React.FC<{ data: TooltipData }> = ({ data }) => {
    // Offset the tooltip to appear above and to the right of the cursor/point
    const x = data.x + 15;
    const y = data.y - 40;
    
    return (
        <div 
            className="absolute p-2 text-sm bg-gray-900/80 dark:bg-black/80 text-white rounded-md shadow-lg pointer-events-none transition-transform"
            style={{ top: `${y}px`, left: `${x}px`, transform: 'translate(-50%, -100%)' }}
        >
            <div className="flex items-center gap-2">
                <span>{data.country.flag}</span>
                <span className="font-semibold">{data.country.name}</span>
                <span className="text-gray-300">{data.country.count} users</span>
            </div>
        </div>
    );
};


